# -*- coding: utf-8 -*- import sys import os print "python :" + sys.version import ConfigParser sys.path.append('/usr/lib/python2.6/site-packages') import dateutil.tz import bioblend from bioblend.galaxy import GalaxyInstance from bioblend.galaxy.histories import HistoryClient from bioblend.galaxy.datasets import DatasetClient sys.path.append('/usr/lib64/python2.6/site-packages') import pandas
print os.getcwd() print u"eXpress_GetDatasetDatPath.py Started......"
inp = sys.argv[1] outp = sys.argv[2] print u"inp: " + inp print u"outp: " + outp
def findDatfilePath(dClient, h_list, hname): dset_list = [dset for dset in [dClient.show_dataset(hst['id']) for hst in h_list] if (dset['file_ext'] == 'txt' or dset['file_ext'] == 'tablur') and '.dat' in dset['file_name'] and 'target_id' in dset['peek']] dset_df = pandas.DataFrame(dset_list) #print dset_df.head(3) dset_path = dset_df[["hid","name","file_name"]] dset_path['History Name !!CAUTION!! If name is WRONG, Please logoff and login again.'] = hname print dset_path.head(3) return dset_path
def dataout(data, filename): data.to_csv(filename, sep="\t")
def get_apikey(): config = ConfigParser.SafeConfigParser() config.read('../../../../local_setting.ini') key = config.get('admin','api_key') return key
def main(): GALAXY_URL = 'http://127.0.0.1:8080/' API_KEY = get_apikey() gInstance = GalaxyInstance(url=GALAXY_URL, key=API_KEY) hClient = HistoryClient(gInstance) dClient = DatasetClient(gInstance)
hst = hClient.get_current_history() hst_cont_all = hClient.show_history(hst['id'], deleted=False, contents=True, visible=True, details=True) hst_cont_ok = [x for x in hst_cont_all if 'eXpress' in x['name'] and x['deleted'] == False and x['state'] == 'ok' ] if (len(hst_cont_ok) > 0): path_list = findDatfilePath(dClient, hst_cont_ok, hst['name']) dataout(path_list, outp) else: print 'eXpress output >>> not found.'
if __name__ == '__main__': main()
print u"....All Done. End of Script"
